import React,{Fragment,useState,useEffect} from 'react';
import Formulario from './components/Formulario';
import Cita from "./components/Cita";


function App() {

  let citasIniciales = localStorage.getItem('citas',JSON.parse(citas));
  if (!citasIniciales) {
    citasIniciales = []
  }

  const [citas, setCitas] = useState(citasIniciales);

  //useEffect para cuando el estado cambia
  useEffect(() => {
    localStorage.setItem('citas',JSON.stringify(citas))
  },[])

  //guarda las citas
  const crearCitas = cita => {
    setCitas([...citas, cita]);
  }

  //funcion para eliminar cita
  const eliminarCita = id => {

    const nuevasCitas = citas.filter(cita => cita.id !== id);
    setCitas(nuevasCitas);
  }

  //Mensaje de condicional de citas
  const tituloCitas = citas.length === 0 ? 'No hay citas' : 'Administra tus citas'; 

  return (
    <Fragment>
      <h1>Administrador de pacientes Veterinarios</h1>
      <div className="container">
        <div className="row">
          <div className="one-half column">
            <Formulario crearCitas={crearCitas} />
          </div>
          <div className="one-half column">
            <h1>{tituloCitas}</h1>
            {citas.map(cita => (
              <Cita 
                key = {cita.id} 
                cita = {cita} 
                eliminarCita = {eliminarCita} 
              />
            ))}
          </div>
        </div>
      </div>
    </Fragment>
  );
}

export default App;
